package com.rahul.accounts.controller;import com.rahul.accounts.dto.CustomerDetailsDto;import com.rahul.accounts.service.ICustomerService;import io.swagger.v3.oas.annotations.Operation;import io.swagger.v3.oas.annotations.media.Content;import io.swagger.v3.oas.annotations.media.Schema;import io.swagger.v3.oas.annotations.responses.ApiResponse;import io.swagger.v3.oas.annotations.responses.ApiResponses;import io.swagger.v3.oas.annotations.tags.Tag;import jakarta.validation.constraints.Pattern;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.validation.annotation.Validated;import org.springframework.web.ErrorResponse;import org.springframework.web.bind.annotation.*;@Tag(        name = "REST API for customer",        description = "REST APIs in bank app for customer details")@RestController@RequestMapping(value = "/v1", produces = MediaType.APPLICATION_JSON_VALUE)@Validatedpublic class CustomerController {    private static final Logger log = LoggerFactory.getLogger(CustomerController.class);    private final ICustomerService customerService;    public CustomerController(ICustomerService customerService) {        this.customerService = customerService;    }    @Operation(            summary = "API to fetch customer details",            description = "bank API to fetch customer details"    )    @ApiResponses({            @ApiResponse(                    responseCode = "200",                    description = "HTTP status OK"            ),            @ApiResponse(                    responseCode = "500",                    description = "HTTP status Internal server error",                    content = @Content(                            schema = @Schema(implementation = ErrorResponse.class)                    )            )    })    @GetMapping("/fetchCustomerDetails")    public ResponseEntity<CustomerDetailsDto> fetchCustomerDetails(@RequestHeader("bank-correlation-id") String correlationId,                                                                   @RequestParam @Pattern(regexp = "(^$|[0-9]{10})",                                                                           message = "Mobile number must be of 10 digits")                                                                   String mobileNumber) {        log.debug("bank correlation id : {}", correlationId);        CustomerDetailsDto customerDetailsDto = customerService.fetchCustomerDetails(mobileNumber, correlationId);        return ResponseEntity.ok().body(customerDetailsDto);    }}