package com.rahul.accounts.entity;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@Entity@Table(name = "accounts") @AllArgsConstructor @NoArgsConstructorpublic class Accounts extends BaseEntity{    @Id    @Column(name = "account_number")    private Long accountNumber;    @Column(name = "customer_id")    private Long customerId;    @Column(name = "branch_address")    private String branchAddress;    @Column(name = "account_type")    private String accountType;}