package com.rahul.accounts.entity;import jakarta.persistence.*;import lombok.*;@Entity@Getter@Setter@ToString@AllArgsConstructor @NoArgsConstructorpublic class Customer extends BaseEntity{    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "customer_id", nullable = false, updatable = false)    private Long customerId;    @Column(unique = true)    private String email;    private String name;    @Column(name = "mobile_number")    private String mobileNumber;}