package com.rahul.cards.repository;import com.rahul.cards.entity.Cards;import jakarta.transaction.Transactional;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import java.util.Optional;public interface CardsRepository extends JpaRepository<Cards, Long> {    Optional<Cards> findCardsByMobileNumber(String mobileNumber);    @Transactional    @Modifying    void deleteCardsByMobileNumber(String mobileNumber);}