package com.rahul.cards.service;import com.rahul.cards.dto.CardsDto;public interface ICardsService {    /**     * Create a cards details for the mobile number     * @param mobileNumber     */    void createCards(String mobileNumber);    /**     *     * @param mobileNumber     * @return Card details     */    CardsDto fetchCards(String mobileNumber);    /**     * updates cards information except card number     * @param cardsDto     * @return true if card details are updated     */    boolean updateCards(CardsDto cardsDto);    /**     * Delete cards details linked to the mobile number     * @param mobileNumber     * @return     */    boolean deleteCards(String mobileNumber);}