package com.rahul.loans.repository;import com.rahul.loans.entity.Loans;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.Optional;@Repositorypublic interface LoansRepository extends JpaRepository<Loans, String> {    Optional<Loans> findByMobileNumber(String mobileNumber);    Optional<Loans> findByLoanNumber(String loanId);}